import React, { useState } from 'react';
import './CreateQr.css';
import QRCode from "qrcode";
const CreateQr = () => {
    const[qrValue, setQrValue]= useState("");
    const[qrImageUrl, setQrImage] = useState("");

    const handleSubmit = async (e) =>{
     e.preventDefault();
     if(!qrValue){
        return alert("Fill the QR value");
     }
     const response =  await QRCode.toDataURL(qrValue);
     console.log(response);
    };
  return (
    <div>
        <h2>Create QR Code</h2>
       <div className="createqr">
        <form onSubmit={handleSubmit}>
        <label className="text">Enter Website:</label>
        <input type="text" className='text' value={qrValue} onChange={(e)=> setQrValue(e.target.value)}/><br/>
        <button type="submit" className="btn5">Create</button>
        </form>
        
       
       </div>
    </div>
  )
}

export default CreateQr
