import {useState} from "react";
import './App.css';

function App() {

  const[name,setName] = useState("");
  const[gender,setGender] = useState("");
  const[dob,setDob] = useState(0);
  const[email,setEmail] = useState("");
  const[phone,setPhone] = useState(0);
  return (
    <div className="App">
     <div class="form">
    <h1>Sign Up</h1>
    <form onsubmit="validateForm()" id="form"> 
        <label>Name: </label><input type="text" id="name" name="Name" pattern="[a-zA-Z]\w{0,16}" placeholder="Enter your  name" required 
        onChange={(event) => { setName(event.target.value);}}/><br/>

        <label>Gender: </label>
        <select id="gender" name="gender" required  
        onChange={(event) => { setGender(event.target.value);}}> 
         <option value="">Select</option>   
         <option value="Male">Male</option>  
         <option value="Female">Female</option> 
         <option value="Other">Other</option>
        </select><br/>
        <label> Date of Birth:</label> 
        <input type="date" id="dob" placeholder="mm-dd-yyyy"
        onfocus="this.max = new Date().toISOString().split('T')['0']" required  
        onChange={(event) => { setDob(event.target.value);}}/><br/>
        <label>Email: </label>
        <input type="email" id="email" name="email"
        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$" placeholder="Enter your  email"  
        onChange={(event) => { setEmail(event.target.value);}}/><br/>
        <label>Phone No: </label>
        <input type="tel" id="phone" pattern="\d{10,10}" placeholder="Enter your  phone no"required  
        onChange={(event) => { setPhone(event.target.value);}}/><br/>
      <div >
        <input  class="button"type="submit" value="Submit" />
      </div>
    </form>
</div>
    </div>
  );
}

export default App;
